// Service Worker for Chubby Unicorn - Offline Support
// DEPRECATED: This service worker is replaced by Vite PWA plugin
// This file is kept for backwards compatibility but will be removed
// The Vite PWA plugin handles all caching with proper versioning

const CACHE_NAME = 'chubby-unicorn-legacy-v1';
const urlsToCache = [];

// Install event - cache assets
self.addEventListener('install', (event) => {
  event.waitUntil(
    caches.open(CACHE_NAME)
      .then((cache) => {
        console.log('Opened cache');
        return cache.addAll(urlsToCache);
      })
      .then(() => self.skipWaiting())
  );
});

// Activate event - clean up old caches
self.addEventListener('activate', (event) => {
  event.waitUntil(
    caches.keys().then((cacheNames) => {
      return Promise.all(
        cacheNames.map((cacheName) => {
          if (cacheName !== CACHE_NAME) {
            console.log('Deleting old cache:', cacheName);
            return caches.delete(cacheName);
          }
        })
      );
    }).then(() => self.clients.claim())
  );
});

// Fetch event - DISABLED to prevent conflicts with Vite PWA
// The Vite PWA plugin handles all caching

// Handle messages from the app
self.addEventListener('message', (event) => {
  if (event.data && event.data.type === 'SKIP_WAITING') {
    self.skipWaiting();
  }
});